import React, { useState, useRef, useEffect, useCallback } from 'react';
import { flushSync } from 'react-dom';
import './ChatInterface.css';
import Sidebar from './Sidebar';
import Message from './Message';
import WelcomeScreen from './WelcomeScreen';
import ChatInput from './ChatInput';
import { chatService } from '../services/chatService';

/**
 * ChatInterface component - Main chat UI component
 * Features modern ChatGPT/Gemini-inspired design
 */
const ChatInterface = () => {
  const [messages, setMessages] = useState([]);
  const [isSending, setIsSending] = useState(false);
  const [showWelcome, setShowWelcome] = useState(true);
  const chatContainerRef = useRef(null);
  const currentResponseRef = useRef('');

  // Auto-scroll to bottom when messages change
  useEffect(() => {
    if (chatContainerRef.current) {
      chatContainerRef.current.scrollTop = chatContainerRef.current.scrollHeight;
    }
  }, [messages]);

  /**
   * Handle sending a message to the backend
   */
  const handleSendMessage = useCallback((text) => {
    if (!text.trim() || isSending) return;

    setShowWelcome(false);
    currentResponseRef.current = '';

    // Add user message
    const userMessage = {
      id: Date.now(),
      text: text.trim(),
      isUser: true,
      isStreaming: false
    };

    const botMessageId = Date.now() + 1;

    // Use flushSync to ensure immediate render
    flushSync(() => {
      setMessages(prev => [...prev, userMessage]);
    });

    setIsSending(true);

    // Add bot message placeholder
    const botMessage = {
      id: botMessageId,
      text: '',
      isUser: false,
      isStreaming: true
    };

    flushSync(() => {
      setMessages(prev => [...prev, botMessage]);
    });

    // Connect to SSE endpoint
    chatService.streamChat(
      text.trim(),
      (chunk) => {
        // Accumulate the response
        currentResponseRef.current += chunk;
        const currentText = currentResponseRef.current;

        // Use flushSync to force immediate state update and render
        flushSync(() => {
          setMessages(prev =>
            prev.map(msg =>
              msg.id === botMessageId
                ? { ...msg, text: currentText }
                : msg
            )
          );
        });
      },
      () => {
        // On complete
        flushSync(() => {
          setMessages(prev =>
            prev.map(msg =>
              msg.id === botMessageId
                ? { ...msg, isStreaming: false }
                : msg
            )
          );
        });
        setIsSending(false);
      },
      (error) => {
        // On error
        console.error('Error streaming chat:', error);
        flushSync(() => {
          setMessages(prev =>
            prev.map(msg =>
              msg.id === botMessageId
                ? { 
                    ...msg, 
                    text: 'Error: Could not connect to the AI service. Please ensure Ollama is running.', 
                    isStreaming: false,
                    isError: true 
                  }
                : msg
            )
          );
        });
        setIsSending(false);
      }
    );
  }, [isSending]);

  /**
   * Handle new chat - clear messages and show welcome screen
   */
  const handleNewChat = useCallback(() => {
    setMessages([]);
    setShowWelcome(true);
    setIsSending(false);
    currentResponseRef.current = '';
  }, []);

  /**
   * Handle suggestion card click
   */
  const handleSuggestionClick = useCallback((prompt) => {
    handleSendMessage(prompt);
  }, [handleSendMessage]);

  return (
    <div className="chat-interface">
      <Sidebar onNewChat={handleNewChat} />

      <main className="main-content">
        <header className="header">
          <div className="header-content">
            <h1>Spring AI Chat</h1>
          </div>
        </header>

        <div className="chat-container" ref={chatContainerRef}>
          <div className="chat-messages">
            {showWelcome ? (
              <WelcomeScreen onSuggestionClick={handleSuggestionClick} />
            ) : (
              messages.map(message => (
                <Message
                  key={message.id}
                  text={message.text}
                  isUser={message.isUser}
                  isStreaming={message.isStreaming}
                  isError={message.isError}
                />
              ))
            )}
          </div>
        </div>

        <ChatInput 
          onSendMessage={handleSendMessage} 
          disabled={isSending} 
        />
      </main>
    </div>
  );
};

export default ChatInterface;
